import { useEffect, useState } from "react";
import axios from "axios";

export default function AmbilDataSpesifik(id) {
    const [dataAPI, setDataAPI] = useState([]);

    const getData = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/databarang/show/"+id);
            setDataAPI(response.data);
        } catch(error) {
            alert(error);
        }
    }

    useEffect(() => {
        getData();
    }, []);

    return JSON.stringify(dataAPI);
}
