import { useEffect, useState } from "react";
import axios from "axios";

export default function AmbilDataSpesifik(data) {
    const [dataAPI, setDataAPI] = useState([]);

    const getData = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/databarang/show/"+data);
            setDataAPI(response.data.barang);
        } catch(error) {
            alert(error);
        }
    }

    useEffect(() => {
        getData();
    }, []);

    const hasil = [dataAPI[0].nama_perangkat, dataAPI[0].jenis, dataAPI[0].status, dataAPI[0].kondisi, dataAPI[0].lokasi];

    return hasil;
}
