import { useEffect, useState } from "react";
import axios from "axios";

export default function AmbilData() {
    const [dataAPI, setDataAPI] = useState([]);
    const [totalAPI, setTotalAPI] = useState([]);

    const getData = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/databarang");
            setDataAPI(response.data);
        } catch(error) {
            console.log(error);
            setDataAPI([
                {
                    id_barang: "-1",
                    nama_perangkat: "Server Down",
                    jenis: "Server Down",
                    jumlah: "Server Down",
                    status: "Server Down",
                    kondisi: "Server Down",
                    lokasi: "Server Down"
                }
            ]);
        }
    }

    const totalData = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/databarang/total");
            setTotalAPI(response.data);
        } catch(error) {
            console.log(error);
            setTotalAPI([
                {
                    total: "-1"
                }
            ]);
        }
    }

    useEffect(() => {
        getData();
        totalData();
    }, []);

    console.log(dataAPI);
    console.log(totalAPI);
    return [dataAPI, totalAPI];
}
